\foreach \angle [count=\xi] in {60,30,...,-270}
  {
  \draw[line width=1pt] (\angle:1.8cm) -- (\angle:2cm);
  \node[font=\large] at (\angle:1.36cm) {\textsf{\xi}};
  }
\foreach \angle in {0,90,180,270}
